
public class Palindrome {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Palindrome numbers between 1 and 100 are:");
		for (int i=1; i<10; i++)
		{
			
			System.out.print(i+" ");	
		}
		
		for (int j=1; j<10; j++) 
		{
				int x = 11*j;
				System.out.print(x+" ");
			}
		}
	}
	